export = asPromise;

type asPromiseCallback = (error: Error | null, ...params: any[]) => {};

/**

 * @memberof util
 * @param {asPromiseCallback} fn Function  call
 * @param {*} ctx
 * @param {...*} params
 * @returns {Promise<*>} P
 */
declare function asPromise(fn: asPromiseCallback, ctx: any, ...params: any[]): Promise<any>;
